mapper.select:
SELECT s.spaceNm, p.EnterDate, p.LeaveDate, p.carNo, p.RecpDt, p.accGubun, p.SaleAmount, p.DiscAmount, p.Gasan
FROM parkdata p
    JOIN space_info s
        ON p.spaceNo = s.spaceNo


        p.carNo = #{carNo}


        p.carNo = null;




        s.spaceNm = #{spaceNm}


        s.spacenm = null;




        s.accGubun = #{accGubun}


        s.accGubun = null;


WHERE p.enterDate LIKE '%' || #{enterDate} || '%'
AND p.leaveDate LIKE '%' || #{outDate} || '%'
